const repeatString = function(str, num) {
  let repeatedStr = "";
  for (i = 0; i < num; i++) {
    repeatedStr += str;
  }
  return repeatedStr;
}

let result = repeatString("hey", 3);

// Do not edit below this line
module.exports = repeatString;
